INSERT INTO `zt_task` VALUES (646, 0, 20, 210, 0, 1, 0, '打印功能优化', 'devel', 0, 0, 0.3, 0, '0000-00-00', 'closed', '', '', '', '<p>支持跨功能模块调用打印功能。</p>\r\n<p>调用打印方案时，支持两个参数：FunctionGUID和<span>FunctionType（打印的业务类型）</span></p>\r\n<p><span>只传<span>FunctionGUID时，直接调用该功能的打印方案，</span></span></p>\r\n<p><span>如果两个都传，就需要通过<span>FunctionGUID和</span><span>FunctionType在</span><span>sys_FunctionPrintSet表中去找目标的FunctionGUID,然后用找到的</span><span>TargetFunctionGUID调用打印方案</span></span></p>\r\n<p><span><br /></span></p>\r\n<p><br /></p>\r\n<p>if exists (select * from sysobjects where name = \'sys_FunctionPrintSet\' and type = \'U\')<br />\r\n&nbsp; &nbsp;DROP TABLE sys_FunctionPrintSet<br />\r\n&nbsp; &nbsp;go<br />\r\n-- =============================================<br />\r\n-- Author:		谷国斌<br />\r\n-- Create date: 2017-10-27<br />\r\n-- Description:	跨功能模块调用打印方案<br />\r\n-- =============================================<br />\r\nCREATE TABLE sys_FunctionPrintSet(<br />\r\nFunctionPrintSetGUID uniqueidentifier not null default (NEWSEQUENTIALID()),<br />\r\nSourceFunctionGUID uniqueidentifier,--原FunctionGUID<br />\r\nFunctionType nvarchar(40),--业务类型，如付款申请、补充合同、合同结算。<br />\r\nTargetFunctionGUID uniqueidentifier,--目标FunctionGUID<br />\r\nprimary key (FunctionPrintSetGUID))<br />\r\nGO<br /></p>', 'gugb', '2017-10-27 17:28:02', 'closed', '2017-12-29 14:23:55', '0000-00-00', '0000-00-00', 'wenj', '2017-11-21 11:48:48', '', '', '0000-00-00 00:00:00', 'gugb', '2017-12-29 14:23:55', 'done', 'gugb', '2017-12-29 14:23:55', '0');
INSERT INTO `zt_task` VALUES (647, 0, 20, 0, 0, 1, 0, '通用角色增加功能授权和数据授权', 'design', 0, 0, 1.7, 0, '0000-00-00', 'closed', '', '', '', '<p><img src=\"data/upload/1/201710/2917054307204p2c.png\" alt=\"\" /></p>\r\n<p><br /></p>\r\n<p>if exists (select * from sysobjects where name = \'sys_GeneralRoleRights\' and type = \'U\')<br />\r\n&nbsp; &nbsp;DROP TABLE sys_GeneralRoleRights<br />\r\n&nbsp; &nbsp;go<br />\r\n-- =============================================<br />\r\n-- Author:		谷国斌<br />\r\n-- Create date: 2017-10-29<br />\r\n-- Description:	通用角色功能权限<br />\r\n-- =============================================<br />\r\nCREATE TABLE sys_GeneralRoleRights(<br />\r\nGeneralRoleRightsGUID uniqueidentifier not null default (NEWSEQUENTIALID()),<br />\r\nGeneralRoleGUID uniqueidentifier,<br />\r\nFunctionGUID uniqueidentifier,<br />\r\nActionCode nvarchar(20),<br />\r\nprimary key (GeneralRoleRightsGUID))<br />\r\nGO<br /></p><div><br /></div>\r\n', 'gugb', '2017-10-29 17:06:33', 'closed', '2020-07-09 15:06:40', '0000-00-00', '2017-11-02', 'jiangy', '2020-06-15 17:03:12', '', '', '0000-00-00 00:00:00', 'wangyt', '2020-07-09 15:06:40', 'done', 'wangyt', '2020-07-09 15:06:40', '0');
INSERT INTO `zt_task` VALUES (648, 0, 9, 0, 0, 1, 0, '业务类型选择界面优化', 'devel', 0, 0, 0.7, 0, '0000-00-00', 'closed', '', '', '', '<img src=\"{2573.png}\" alt=\"\" /><br /><br />\r\nIF NOT EXISTS<br />\r\n(<br />\r\n&nbsp; &nbsp; SELECT *<br />\r\n&nbsp; &nbsp; FROM syscolumns<br />\r\n&nbsp; &nbsp; WHERE id = OBJECT_ID(\'wf_BizType\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'SystemGUID\'<br />\r\n)<br />\r\n&nbsp; &nbsp;Alter table wf_BizType Add SystemGUID uniqueidentifier<br />\r\nGO<br /><br /><br />\r\nIF NOT EXISTS<br />\r\n(<br />\r\n&nbsp; &nbsp; SELECT *<br />\r\n&nbsp; &nbsp; FROM syscolumns<br />\r\n&nbsp; &nbsp; WHERE id = OBJECT_ID(\'wf_BizType\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'OrderCode\'<br />\r\n)<br />\r\n&nbsp; &nbsp;Alter table wf_BizType Add OrderCode nvarchar(20)<br />\r\nGO<br /><br /><br />\r\nupdate wf_BizType set SystemGUID = \'94EFFAFC-3ECA-E511-B30C-000C292F5F0C\' where SystemCode = \'0201\'<br /><br />\r\nupdate wf_BizType set SystemGUID = \'A309E787-3ECA-E511-B30C-000C292F5F0C\' where SystemCode = \'0202\'<br /><div><br /></div>', 'gugb', '2017-11-01 09:35:46', 'closed', '2017-11-22 09:03:22', '0000-00-00', '0000-00-00', 'chenl', '2017-11-01 15:58:57', '', '', '0000-00-00 00:00:00', 'gugb', '2017-11-22 09:03:22', 'done', 'gugb', '2017-11-22 09:03:22', '0');
