INSERT INTO `zt_task` VALUES (664, 0, 8, 0, 0, 1, 0, '成本和费用的合同付款申请界面，调整为通过p_ebs_GetContPaymentType存储过程来获取付款审批类型，对比原来增加了归属（成本or费用）的属性。', 'devel', 0, 0, 0.1, 0, '0000-00-00', 'closed', '', '', '', '<p>注：此存储过程已加入升级脚本。</p>\r\n<p><span>Ascription变量来自于合同</span></p>\r\n<br />\r\nif exists(select * from sysobjects where &nbsp;name = \'p_ebs_GetContPaymentType\' and type = \'P\')<br /><br />\r\nDROP PROCEDURE &nbsp;p_ebs_GetContPaymentType<br />\r\ngo<br /><br /><br />\r\n-- =============================================<br />\r\n-- Author:		谷国斌<br />\r\n-- Create date: 2015-12-08<br />\r\n-- Description:	获取合同付款审批类型方式<br />\r\n-- =============================================<br /><br /><br />\r\nCREATE PROCEDURE p_ebs_GetContPaymentType<br />\r\n(<br />\r\n@CompanyGUID uniqueidentifier,<br />\r\n@UserGUID uniqueidentifier,<br />\r\n@PaymentClass varchar(20),<br />\r\n@Ascription nvarchar(20)<br />\r\n)<br />\r\nAS<br />\r\nBEGIN<br />\r\nSET NOCOUNT ON;<br />\r\nSELECT distinct tp.PaymentTypeGUID, ParentGUID, tp.CompanyGUID, PaymentTypeCode, PaymentTypeName, PaymentClass, ProcessGUID, LoanType, Explaination<br />\r\nFROM ebs_PaymentType tp inner join ebs_PaymentTypeRights &nbsp;rights<br />\r\non tp.PaymentTypeGUID = rights.PaymentTypeGUID<br />\r\ninner join v_sys_StationUser su on rights.StationGUID = su.StationGUID&nbsp;<br />\r\nwhere PaymentClass = @PaymentClass and tp.CompanyGUID = @CompanyGUID and su.UserGUID = @UserGUID and Ascription = @Ascription<br /><br />\r\nSET NOCOUNT OFF;<br />\r\nEND<br /><br />\r\ngo<br /><br />', 'gugb', '2017-11-22 08:36:50', 'closed', '2017-12-29 14:21:35', '0000-00-00', '0000-00-00', 'dssy', '2017-11-23 11:23:22', '', '', '0000-00-00 00:00:00', 'gugb', '2017-12-29 14:21:35', 'done', 'gugb', '2017-12-29 14:21:35', '0');
