INSERT INTO `zt_task` VALUES (678, 0, 17, 0, 0, 1, 0, '财务付款申请重新选择审批类型。（来源于注册的通用类别功能，参考变更、保证金中的类型）', 'devel', 1, 0, 0.9, 0, '0000-00-00', 'closed', '', '', '', '<img src=\"{2813.png}\" alt=\"\" /><br />\r\ndelete from wf_BizType where BizTypeGUID = \'0C4AEACB-F7FA-E511-BB12-000C292F5F0C\'<br />\r\nInsert into wf_BizType(BizTypeGUID,BizTypeName,BizTypeContrast,SystemGUID,OrderCode)<br />\r\nValues(\'0C4AEACB-F7FA-E511-BB12-000C292F5F0C\',\'财务付款申请审批\',\'财务付款申请类型\',\'94EFFAFC-3ECA-E511-B30C-000C292F5F0C\',\'0205\')<br /><br /><br />\r\nDeclare @GeneralTypeGUID uniqueidentifier<br /><br />\r\n--财务付款申请<br />\r\nset @GeneralTypeGUID = \'5914DAFC-4CDA-E711-83BC-000C293C2531\'<br />\r\ndelete from sys_GeneralType where GeneralTypeGUID = @GeneralTypeGUID<br />\r\nInsert into sys_GeneralType (GeneralTypeGUID,GeneralTypeCode,GeneralTypeName,ScopeType,MaxLevel,SystemGUIDList,SystemNameList)<br />\r\nValues(@GeneralTypeGUID,\'ebs_FinPayType\',\'财务付款类型\',\'公司级\',1,\'a309e787-3eca-e511-b30c-000c292f5f0c,94effafc-3eca-e511-b30c-000c292f5f0c\',\'【成本管理】,【费用预算管理】\')<br /><br />\r\ndelete from sys_GeneralTypeFlowType where GeneralTypeGUID=@GeneralTypeGUID<br />\r\nInsert into sys_GeneralTypeFlowType(GeneralTypeGUID,GeneralTypeFlowCode,GeneralTypeFlowName,BizTypeGUID,BizTypeName)<br />\r\nvalues(@GeneralTypeGUID,\'01\',\'财务付款\',\'0C4AEACB-F7FA-E511-BB12-000C292F5F0C\',\'财务付款申请审批\')<br /><p><br /></p>\r\n<p><br /></p>\r\n<p><br />\r\n--财务付款申请增加审批类型<br />\r\nIF NOT EXISTS ( SELECT &nbsp;*<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM &nbsp; &nbsp;syscolumns<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE &nbsp; id = OBJECT_ID(\'ebs_ContractPayApply\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'FINPayTypeGUID\' )<br />\r\n&nbsp; &nbsp; ALTER TABLE ebs_ContractPayApply ADD FINPayTypeGUID uniqueidentifier<br />\r\n&nbsp; &nbsp;&nbsp;<br /><br /><br />\r\n--财务付款申请增加审批类型名称<br />\r\nIF NOT EXISTS ( SELECT &nbsp;*<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM &nbsp; &nbsp;syscolumns<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE &nbsp; id = OBJECT_ID(\'ebs_ContractPayApply\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'FINPayTypeName\' )<br />\r\n&nbsp; &nbsp; ALTER TABLE ebs_ContractPayApply ADD FINPayTypeName nvarchar(50)<br />\r\nGO<br /><br />\r\n--财务付款申请增加申请日期<br />\r\nIF NOT EXISTS ( SELECT &nbsp;*<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM &nbsp; &nbsp;syscolumns<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE &nbsp; id = OBJECT_ID(\'ebs_ContractPayApply\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'FINPayApplyDate\' )<br />\r\n&nbsp; &nbsp; ALTER TABLE ebs_ContractPayApply ADD FINPayApplyDate date<br />\r\nGO<br /><br /><br />\r\n--财务付款申请增加财务申请审核日期<br />\r\nIF NOT EXISTS ( SELECT &nbsp;*<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM &nbsp; &nbsp;syscolumns<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE &nbsp; id = OBJECT_ID(\'ebs_ContractPayApply\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'FINPayApproveDate\' )<br />\r\n&nbsp; &nbsp; ALTER TABLE ebs_ContractPayApply ADD FINPayApproveDate date<br />\r\nGO<br /><br />\r\n--财务付款申请增加财务申请审核人GUID<br />\r\nIF NOT EXISTS ( SELECT &nbsp;*<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM &nbsp; &nbsp;syscolumns<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE &nbsp; id = OBJECT_ID(\'ebs_ContractPayApply\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'FINPayApproveBy\' )<br />\r\n&nbsp; &nbsp; ALTER TABLE ebs_ContractPayApply ADD FINPayApproveBy uniqueidentifier<br />\r\nGO<br /><br />\r\n--财务付款申请增加财务申请审核人名称<br />\r\nIF NOT EXISTS ( SELECT &nbsp;*<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM &nbsp; &nbsp;syscolumns<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE &nbsp; id = OBJECT_ID(\'ebs_ContractPayApply\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'FINPayApproveByName\' )<br />\r\n&nbsp; &nbsp; ALTER TABLE ebs_ContractPayApply ADD FINPayApproveByName nvarchar(20)<br />\r\nGO<br /></p><div><br /></div>\r\n', 'gugb', '2017-12-06 14:31:58', 'closed', '2020-03-12 11:14:18', '0000-00-00', '0000-00-00', 'dssy', '2017-12-07 17:18:38', '', '', '0000-00-00 00:00:00', 'xuf', '2020-03-12 11:14:18', 'done', 'xuf', '2020-03-12 11:14:18', '0');
INSERT INTO `zt_task` VALUES (679, 0, 20, 0, 0, 1, 0, '登录页和个人信息中增加二维码扫码功能。', 'devel', 1, 0, 0.7, 0, '0000-00-00', 'closed', '', '', '', '<p>declare @systemGUID uniqueidentifier<br />\r\nset @systemGUID = \'CD8165C4-3DCA-E511-B30C-000C292F5F0C\' <br />\r\ndeclare @ParamGroupGUID uniqueidentifier<br /><br />\r\nselect &nbsp;@ParamGroupGUID = ParamGroupGUID from sys_BizParamGroup where GroupName = \'系统个性化\' and SystemGUID = @SystemGUID<br /><br />\r\ndelete from sys_BizParamRegist where ParamName = \'sys_qywxcode\'<br /><br />\r\nInsert into sys_BizParamRegist(ParamName, ParamNameChn, ParamGroupGUID, ScopeType, ParamType, ParamWidth, LevelLimit, OrderCode, ShowType, Options, ParamDescription, ActionCode)<br />\r\nvalues(\'sys_qywxcode\',\'企业微信二维码\',@ParamGroupGUID,\'集团\',\'参数配置\',NULL,NULL,\'0006\',\'Text\',\'\',\'企业微信二维码\',\'0006\')<br /><br /><br />\r\ndelete from sys_BizParamRegist where ParamName = \'sys_qyhcode\'<br />\r\nInsert into sys_BizParamRegist(ParamName, ParamNameChn, ParamGroupGUID, ScopeType, ParamType, ParamWidth, LevelLimit, OrderCode, ShowType, Options, ParamDescription, ActionCode)<br />\r\nvalues(\'sys_qyhcode\',\'微信企业号二维码\',@ParamGroupGUID,\'集团\',\'参数配置\',NULL,NULL,\'0007\',\'Text\',\'\',\'微信企业号二维码\',\'0007\')<br /><br /></p>\r\n<p><img src=\"{2817.png}\" alt=\"\" /></p>\r\n<p><br /></p>\r\n<p>登录后的扫码：</p>\r\n<p>放置在消息和用户名之间，增加一个字体图标<span style=\"font-size:12px;\">：</span><span style=\"color:#222222;font-family:\'museo-slab\', Georgia;\">fa-mobile，字体颜色：白色。</span><br /><span></span></p>\r\n<p><span><br /></span></p>\r\n<p><span>企业微信图片显示内容：扫描二维码加入企业</span></p>\r\n<p><span>微信企业号图片显示内容：扫描二维码关注企业号</span></p>\r\n<p><span><br /></span></p>\r\n<p><span>图片展示时，固定大小，高和宽为160px左右。</span></p>\r\n<p><span><br /></span></p>\r\n<p>如果两个地址为空，则主页不显示图标，首页上也不显示。</p>\r\n<p>如果只填了其中一个地址，则显示一个图标内容。没有填的不显示。<img src=\"{2816.png}\" alt=\"\" /></p>', 'gugb', '2017-12-06 16:45:04', 'closed', '2017-12-29 14:14:50', '0000-00-00', '0000-00-00', 'xuf', '2017-12-08 14:37:40', '', '', 