INSERT INTO `zt_task` VALUES (701, 0, 8, 31, 0, 1, 0, '费用预算审核中，对公司整体预算增加审批。（采用与预算部门审批相同的业务参数进行控制线下和线上。使用不同的业务对象。）', 'devel', 0, 0, 1.4, 0, '0000-00-00', 'closed', '', '', '', '<p><img src=\"{3035.png}\" alt=\"\" /></p>\r\n<p><br />\r\nif exists (select * from sysobjects where name = \'v_ebs_YearCompanyBudget\' and type = \'v\')<br />\r\n&nbsp; &nbsp;drop view dbo.v_ebs_YearCompanyBudget<br />\r\n&nbsp; &nbsp;go<br /><br />\r\nCREATE View v_ebs_YearCompanyBudget<br />\r\nas<br />\r\n-- =============================================<br />\r\n-- Author:		谷国斌<br />\r\n-- Create date: 2018-1-4<br />\r\n-- Description:	公司预算视图<br />\r\n-- =============================================<br />\r\n&nbsp;<br />\r\nSELECT &nbsp;b.CompanyGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; a.SubjectGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.ParentCode ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectCode ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectShortCode ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectShortName ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.Remarks ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.IsEndSubject ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectLevel ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectYear ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount01) AS PlanAmount01 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount02) AS PlanAmount02 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount03) AS PlanAmount03 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount04) AS PlanAmount04 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount05) AS PlanAmount05 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount06) AS PlanAmount06 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount07) AS PlanAmount07 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount08) AS PlanAmount08 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount09) AS PlanAmount09 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount10) AS PlanAmount10 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount11) AS PlanAmount11 ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SUM(a.PlanAmount12) AS PlanAmount12<br />\r\nFROM &nbsp; &nbsp;dbo.ebs_YearDptBudget a<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; INNER JOIN dbo.ebs_YearDepartment b ON a.DepartmentGUID = b.DepartmentGUID<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; INNER JOIN dbo.ebs_YearSubject c ON a.SubjectGUID = c.SubjectGUID<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; INNER JOIN dbo.ebs_YearBudgetStatus d ON b.DepartmentGUID=d.DepartmentGUID<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; WHERE d.ApproveStatus=\'已审核\'<br />\r\nGROUP BY b.CompanyGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; a.SubjectGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.ParentCode ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectCode ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectShortCode ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectShortName ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.Remarks ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.IsEndSubject ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectLevel ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; c.SubjectYear<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; go &nbsp; &nbsp; &nbsp;<br /><br /><br />\r\nif exists (select * from sysobjects where name = \'ebs_YearCompayBudgetStatus\' and type = \'U\')<br />\r\n&nbsp; &nbsp;DROP TABLE ebs_YearCompayBudgetStatus<br />\r\n&nbsp; &nbsp;go<br />\r\n-- =============================================<br />\r\n-- Author:		谷国斌<br />\r\n-- Create date: 2018-1-4<br />\r\n-- Description:	公司预算审核状态表<br />\r\n-- =============================================<br />\r\nCREATE TABLE ebs_YearCompayBudgetStatus(<br />\r\nYearBgtStatGUID uniqueidentifier not null default (NEWSEQUENTIALID()),<br />\r\nCompanyGUID uniqueidentifier, <br />\r\nApproveStatus nvarchar(10),<br />\r\nReportedBy uniqueidentifier,<br />\r\nReportDate datetime,<br />\r\nApproveBy uniqueidentifier,<br />\r\nApproveByName nvarchar(10),<br />\r\nApproveDate datetime<br />\r\nprimary key (YearBgtStatGUID))<br /><br />\r\nGO<br /><br /><br /><br />\r\n--增加公司年度费用预算审批业务类型<br />\r\ndeclare @systemGUID uniqueidentifier<br />\r\nset @systemGUID = \'94EFFAFC-3ECA-E511-B30C-000C292F5F0C\'<br /><br />\r\nDELETE FROM wf_BizType WHERE BizTypeGUID=\'8BE64F1C-F7FA-E511-BB12-000C292F5F0C\'<br />\r\nInsert into wf_BizType(BizTypeGUID,BizTypeName,BizTypeContrast,systemGUID,OrderCode)<br />\r\nvalues(\'8BE64F1C-F7FA-E511-BB12-000C292F5F0C\',\'公司年度费用预算审批\',\'公司年度费用预算\',@systemGUID,\'1000\')<br /><br />\r\ngo<br />\r\n&nbsp;<br /></p>', 'gugb', '2018-01-04 11:04:00', 'closed', '2018-01-23 14:12:13', '0000-00-00', '0000-00-00', 'dssy', '2018-01-10 22:00:31', '', '', '0000-00-00 00:00:00', 'gugb', '2018-01-23 14:12:13', 'done', 'gugb', '2018-01-23 14:12:13', '0');
