INSERT INTO `zt_task` VALUES (789, 0, 18, 0, 0, 1, 0, '通用类别注册增加生成SQL脚本功能', 'devel', 0, 0, 0, 0, '0000-00-00', 'closed', '', '', '', '<br /><br />\r\nif exists(select * from sysobjects where &nbsp;name = \'p_sys_GenerateGeneralTypeScript\' and type = \'P\')<br /><br />\r\nDROP PROCEDURE &nbsp;p_sys_GenerateGeneralTypeScript<br />\r\ngo<br /><br /><br />\r\n-- =============================================<br />\r\n-- Author:		谷国斌<br />\r\n-- Create date: 2018-2-8<br />\r\n-- Description:	生成通用类别的相关SQL脚本<br />\r\n-- =============================================<br /><br /><br />\r\nCREATE PROCEDURE p_sys_GenerateGeneralTypeScript<br />\r\n(<br />\r\n@GeneralTypeGUID UNIQUEIDENTIFIER<br />\r\n)<br />\r\n--WITH Encryption<br />\r\nAS<br />\r\nBEGIN<br />\r\nSET NOCOUNT ON;<br /><br /><br />\r\nCREATE TABLE #TempTalbe(ID INT IDENTITY(1,1),Script nvarchar(max))<br /><br />\r\n--1.sys_GeneralType表<br /><br /><br />\r\nINSERT INTO #TempTalbe(Script) VALUES(\'--初始化1.sys_GeneralType表\')<br />\r\nINSERT INTO #TempTalbe(Script) VALUES(\'Delete from sys_GeneralType where GeneralTypeGUID=\'\'\'+CAST(@GeneralTypeGUID AS NVARCHAR(40))+\'\'\'\')<br />\r\nSELECT * INTO #sys_GeneralType FROM sys_GeneralType WHERE GeneralTypeGUID=@GeneralTypeGUID<br />\r\nINSERT INTO #TempTalbe(Script)&nbsp;<br />\r\nEXEC p_sys_InsertScriptFromTemp \'#sys_GeneralType\',\'sys_GeneralType\'<br />\r\nDROP TABLE #sys_GeneralType<br /><br />\r\n--2.sys_GeneralTypeFlowType表<br />\r\nINSERT INTO #TempTalbe(Script) VALUES(\'--初始化2.sys_GeneralTypeFlowType表\')<br />\r\nINSERT INTO #TempTalbe(Script) VALUES(\'Delete from sys_GeneralTypeFlowType where GeneralTypeGUID=\'\'\'+CAST(@GeneralTypeGUID AS NVARCHAR(40))+\'\'\'\')<br />\r\nSELECT * INTO #sys_GeneralTypeFlowType FROM sys_GeneralTypeFlowType WHERE GeneralTypeGUID=@GeneralTypeGUID<br />\r\nINSERT INTO #TempTalbe(Script)&nbsp;<br />\r\nEXEC p_sys_InsertScriptFromTemp \'#sys_GeneralTypeFlowType\',\'sys_GeneralTypeFlowType\'<br />\r\nDROP TABLE #sys_GeneralTypeFlowType<br /><br />\r\n--2.sys_GeneralTypeProperty表<br />\r\nINSERT INTO #TempTalbe(Script) VALUES(\'--初始化3.sys_GeneralTypeProperty表\')<br />\r\nINSERT INTO #TempTalbe(Script) VALUES(\'Delete from sys_GeneralTypeProperty where GeneralTypeGUID=\'\'\'+CAST(@GeneralTypeGUID AS NVARCHAR(40))+\'\'\'\')<br />\r\nSELECT * INTO #sys_GeneralTypeProperty FROM sys_GeneralTypeProperty WHERE GeneralTypeGUID=@GeneralTypeGUID<br />\r\nINSERT INTO #TempTalbe(Script)&nbsp;<br />\r\nEXEC p_sys_InsertScriptFromTemp \'#sys_GeneralTypeProperty\',\'sys_GeneralTypeProperty\'<br />\r\nDROP TABLE #sys_GeneralTypeProperty<br /><br /><br />\r\nSELECT Script FROM #TempTalbe ORDER BY ID<br />\r\nDROP TABLE #TempTalbe<br />\r\nSET NOCOUNT OFF;<br /><br />\r\nEND<br />\r\nGO<br /><br /><br />', 'gugb', '2018-06-16 12:07:30', 'closed', '2018-08-31 17:43:41', '0000-00-00', '0000-00-00', 'xiongzj', '2018-06-19 18:56:52', '', '', '0000-00-00 00:00:00', 'xiongzj', '2018-08-31 17:43:41', 'done', 'xiongzj', '2018-08-31 17:43:41', '0');
