INSERT INTO `zt_task` VALUES (795, 0, 22, 181, 0, 1, 0, '小订管理中根据业务参数配置的款项名称生成应收款。', 'devel', 1, 0, 0.1, 0, '0000-00-00', 'closed', '', '', '', '<p><br /></p>\r\n<p><br />\r\n--销售管理系统&nbsp;<br />\r\ndeclare @systemGUID uniqueidentifier<br />\r\nset @systemGUID = \'C5277002-3ECA-E511-B30C-000C292F5F0C\'<br /><br />\r\nDECLARE @ParamGroupGUID uniqueidentifier<br /><br />\r\nSELECT &nbsp;@ParamGroupGUID = ParamGroupGUID from sys_BizParamGroup where GroupName = \'3.交易管理\' and SystemGUID = @SystemGUID<br /><br />\r\nDELETE FROM sys_BizParamRegist WHERE ParamName=\'sms_IntentItemName\'<br />\r\nINSERT into sys_BizParamRegist(ParamGUID,ParamName, ParamNameChn, ParamGroupGUID, ScopeType, ParamType, ParamWidth, LevelLimit, OrderCode, ShowType, Options, ParamDescription)<br />\r\nvalues(\'BF4B0C01-5574-E811-A829-00505629CC36\',\'sms_IntentItemName\',\'小订款项名称\',@ParamGroupGUID,\'项目\',\'参数配置\',NULL,NULL,\'4005\',\'Text\',\'\',\'\')<br /><br /><br />\r\ndelete from [sys_BizParamOptionTemplet] where [ParamName] = \'sms_IntentItemName\'<br />\r\nINSERT [dbo].[sys_BizParamOptionTemplet] ([ParamName], [ParamValue])&nbsp;<br />\r\nVALUES (N\'sms_IntentItemName\',\'订金\')<br /><br /><br />\r\ndelete FROM dbo.sys_BizParamOption WHERE ParamGUID =\'BF4B0C01-5574-E811-A829-00505629CC36\'<br /><br />\r\nInsert into sys_BizParamOption(ParamGUID, ScopeGUID, ParamValue, ParamCode, ParentCode, ParamLevel, IsEndLevel, IsSysParam)<br />\r\nselect ParamGUID,bu.ProjGUID,ParamValue,ParamCode,tp.ParentCode,ParamLevel,IsEndLevel,IsSysParam&nbsp;<br />\r\nfrom sys_BizParamRegist re inner join sys_BizParamOptionTemplet tp&nbsp;<br />\r\non re.ParamName = tp.ParamName&nbsp;<br />\r\ninner join dbo.mdm_Project bu on bu.Level = 1<br />\r\nInner join sys_BizParamGroup &nbsp;p<br />\r\non re.ParamGroupGUID = p.ParamGroupGUID&nbsp;<br />\r\nwhere ScopeType = \'项目\' and ParamType in (\'参数配置\',\'列表选项\')&nbsp;<br />\r\nAND &nbsp;re.ParamName =\'sms_IntentItemName\'<br /></p>', 'gugb', '2018-06-20 14:46:16', 'clos