INSERT INTO `zt_task` VALUES (809, 0, 9, 54, 232, 1, 0, '增加扩展类参数，公司级。用于设置各公司的工作日。', 'devel', 0, 0, 0, 0, '2018-07-09', 'closed', '', '', '', '<p>1.选择该公司后，如果该公司下没有数据，则初始化星期一至星期日的数据（默认周六和周日不选中）。</p>\r\n<p><br /></p>\r\n<p>if exists (select * from sysobjects where name = \'wf_WorkDaySet\' and type = \'U\')<br />\r\n&nbsp; &nbsp;DROP TABLE wf_WorkDaySet<br />\r\n&nbsp; &nbsp;go<br />\r\n-- =============================================<br />\r\n-- Author:		谷国斌<br />\r\n-- Create date: 2018-7-6<br />\r\n-- Description:	工作日设置表<br />\r\n-- =============================================<br />\r\nCREATE TABLE wf_WorkDaySet(<br />\r\nWorkDaySetGUID uniqueidentifier not null default (NEWSEQUENTIALID()),<br />\r\nCompanyGUID uniqueidentifier,<br />\r\nWeekname NVARCHAR(10),<br />\r\nisWork BIT,&nbsp;<br />\r\nprimary key (WorkDaySetGUID))<br /><br />\r\nGO</p>\r\n<div><br /></div>\r\n如果表中没有数据，默认按周六和周日为休息日处理。<p><br /></p>\r\n<p><img src=\"{4866.png}\" alt=\"\" /></p>', 'xianxy', '2018-07-09 16:29:15', 'closed', '2020-03-12 11:08:47', '2018-07-09', '2018-07-09', 'xianxy', '2018-07-09 16:30:05', '', '', '0000-00-00 00:00:00', 'xuf', '2020-03-12 11:08:47', 'done', 'xuf', '2020-03-12 11:08:47', '0');
INSERT INTO `zt_task` VALUES (810, 0, 30, 127, 216, 1, 0, '收入合同增加合同属性字段。属性取值来自于选择的合同类别上的属性，新增合同时的Url地址上根据属性进行过滤。', 'devel', 0, 0, 1, 0, '2018-07-06', 'closed', '', '', '', '<p>1.新增合同的Url地址，增加合同属性。通过合同属性，在新增合同界面选择时，只选择该属性的合同类别。</p>\r\n<p>2.保存合同时，将该 合同类别的属性一并保存。</p>\r\n<p><br /></p>\r\n<p><br /></p>\r\n<p>以下脚本已提交升级脚本：</p>\r\n<p><br />\r\n--初始化1.sys_GeneralType表<br />\r\nDelete from sys_GeneralType where GeneralTypeGUID=\'24E8230E-2400-E811-B442-000C293C2531\'<br />\r\ninsert into sys_GeneralType(GeneralTypeGUID,GeneralTypeCode,GeneralTypeName,ScopeType,MaxLevel,Remarks,SystemGUIDList,SystemNameList) values (\'24E8230E-2400-E811-B442-000C293C2531\',\'ims_ContractType\',\'收入合同类别\',\'公司级\',8,NULL,\'88A41B07-3ECA-E511-B30C-000C292F5F0C\',\'【收入管理】\')<br />\r\n--初始化2.sys_GeneralTypeFlowType表<br />\r\nDelete from sys_GeneralTypeFlowType where GeneralTypeGUID=\'24E8230E-2400-E811-B442-000C293C2531\'<br />\r\ninsert into sys_GeneralTypeFlowType(GeneralTypeFlowGUID,GeneralTypeGUID,GeneralTypeFlowCode,GeneralTypeFlowName,BizTypeGUID,BizTypeName,Remarks,FlowType) values (\'CB670D8E-AB68-E811-99F4-00505629CC36\',\'24E8230E-2400-E811-B442-000C293C2531\',\'01\',\'合同审批\',\'03E64F1C-AD54-4CBB-9C54-8D7C39C33B3F\',\'收入合同审批\',NULL,NULL)<br />\r\ninsert into sys_GeneralTypeFlowType(GeneralTypeFlowGUID,GeneralTypeGUID,GeneralTypeFlowCode,GeneralTypeFlowName,BizTypeGUID,BizTypeName,Remarks,FlowType) values (\'CC670D8E-AB68-E811-99F4-00505629CC36\',\'24E8230E-2400-E811-B442-000C293C2531\',\'02\',\'产值审批\',\'02E64F1C-AD54-4CBB-9C54-8D7C39C33B3F\',\'收入合同产值审批\',NULL,NULL)<br />\r\ninsert into sys_GeneralTypeFlowType(GeneralTypeFlowGUID,GeneralTypeGUID,GeneralTypeFlowCode,GeneralTypeFlowName,BizTypeGUID,BizTypeName,Remarks,FlowType) values (\'CD670D8E-AB68-E811-99F4-00505629CC36\',\'24E8230E-2400-E811-B442-000C293C2531\',\'03\',\'开票申请\',\'01E64F1C-F7FA-E511-BB12-000C292F5F0C\',\'收入发票审批\',NULL,NULL)<br />\r\n--初始化3.sys_GeneralTypeProperty表<br />\r\nDelete from sys_GeneralTypeProperty where GeneralTypeGUID=\'24E8230E-2400-E811-B442-000C293C2531\'<br />\r\ninsert into sys_GeneralTypeProperty(GeneralTypePropertyGUID,GeneralTypeGUID,PropertyName,PropertyCode,InputType,OrderNumber,Options) values (\'0C6E15C0-E279-E811-A829-00505629CC36\',\'24E8230E-2400-E811-B442-000C293C2531\',\'合同属性\',\'ContractProperty\',\'radio\',0,\'收入,收入合同;基建,基建合同\')<br /><br />\r\ngo<br />\r\n&nbsp;&nbsp;<br /><br />\r\n--收入合同增加合同属性字段<br />\r\nIF NOT EXISTS ( SELECT &nbsp;*<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM &nbsp; &nbsp;syscolumns<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE &nbsp; id = OBJECT_ID(\'ims_Contract\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'ContractProperty\' )<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; ALTER TABLE ims_Contract ADD ContractProperty NVARCHAR(100);<br /><br />\r\nGO<br /><br />\r\nUPDATE ims_Contract SET ContractProperty = \'收入\' WHERE ContractProperty IS NULL<br /><br />\r\ngo</p>\r\n<div><br /></div>', 'lic', '2018-07-09 16:37:09', 'closed', '2020-04-16 16:21:52', '2018-07-06', '0000-00-00', 'lic', '2020-04-16 16:21:48', '', '', '0000-00-00 00:00:00', 'lic', '2020-04-16 16:21:52', 'done', 'lic', '2020-04-16 16:21:52', '0');
