INSERT INTO `zt_task` VALUES (824, 0, 29, 16, 226, 1, 0, '立项申请界面，在预算分摊列表中增加列：查看预算使用情况。执行存储过程返回展示的内容', 'devel', 2, 0, 0, 0, '0000-00-00', 'closed', '', '', '', '<p><img src=\"{4846.png}\" alt=\"\" /></p>\r\n<p><br /></p>\r\n<p><img src=\"{4845.png}\" alt=\"\" /></p>\r\n<p><br /></p>\r\n<p>执行以下存储过程 ：</p>\r\n<p>注：该存储过程没有编写完，现在只有部分，但不影响开发。<br />\r\nif exists(select * from sysobjects where &nbsp;name = \'p_ebs_GetBudgetUseDtl\' and type = \'P\')<br /><br />\r\nDROP PROCEDURE &nbsp;p_ebs_GetBudgetUseDtl<br />\r\ngo<br />\r\n&nbsp;<br /><br />\r\n-- =============================================<br />\r\n-- Author:		谷国斌<br />\r\n-- Create date: 2018-7-4<br />\r\n-- Description:	获取指定预算部门科目使用详情<br />\r\n-- =============================================<br /><br /><br />\r\nCREATE PROCEDURE p_ebs_GetBudgetUseDtl<br />\r\n(<br />\r\n@SubjectGUID &nbsp;uniqueidentifier,<br />\r\n@DepartmentGUID uniqueidentifier,<br />\r\n@Month NVARCHAR(10),<br />\r\n@UseCase &nbsp;nvarchar(10)=null--立项,签约,付款<br />\r\n)<br />\r\nAS<br />\r\nBEGIN<br />\r\nSET NOCOUNT ON;<br /><br />\r\nDECLARE @ControlRule NVARCHAR(50),@ControlCycle NVARCHAR(50)<br /><br />\r\nSELECT @ControlRule=ControlRule,@ControlCycle=ControlCycle FROM dbo.ebs_YearSubject WHERE SubjectGUID=@SubjectGUID<br /><br />\r\nCREATE TABLE #UseDtl(ID INT IDENTITY(1,1),Title NVARCHAR(200),Remarks NVARCHAR(200))<br /><br />\r\nINSERT INTO #UseDtl<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;( Title, Remarks )<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;SELECT \'预算部门\',CAST(a.DepartmentYear AS NVARCHAR)+\'年-\'+b.OrgName+\'-\'+a.DepartmentName FROM dbo.ebs_YearDepartment a INNER JOIN dbo.sys_BusinessUnit b ON a.CompanyGUID=b.OrgGUID&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;WHERE a.DepartmentGUID=@DepartmentGUID<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;UNION ALL&nbsp;<br />\r\nSELECT \'科目名称\',\'[\'+SubjectKind+\']\'+SubjectShortName FROM dbo.ebs_YearSubject WHERE SubjectGUID=@SubjectGUID<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'控制周期\',@ControlCycle<br /><br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'余额使用方式\',@ControlRule<br /><br />\r\nIF @UseCase=\'立项\'<br />\r\nBEGIN<br />\r\nINSERT INTO #UseDtl<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp;( Title, Remarks )<br />\r\nSELECT \'是否立项管控\',CASE WHEN IsApprovalCtl=1 THEN \'是\' ELSE \'否\' end FROM dbo.ebs_YearSubject WHERE SubjectGUID=@SubjectGUID<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'全年预算\',CONVERT(NVARCHAR,SUM(PlanAmount+AdjustAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'全年累计已立项\',CONVERT(NVARCHAR,SUM(MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'全年累计余额\',CONVERT(NVARCHAR,SUM(PlanAmount+AdjustAmount-MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID<br /><br />\r\nIF @ControlRule=\'滚动使用\'<br />\r\nBEGIN<br />\r\nIF @ControlCycle=\'月\'<br /><br />\r\nINSERT INTO #UseDtl( Title, Remarks )<br />\r\nSELECT \'当月可用预算\',CONVERT(NVARCHAR,SUM(CASE WHEN BudgetMonth=@Month THEN PlanAmount+AdjustAmount end)+SUM(CASE WHEN BudgetMonth&lt;@Month THEN PlanAmount+AdjustAmount-MatterAmount end),1)&nbsp;<br />\r\nFROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当月已立项\',CONVERT(NVARCHAR,SUM(MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID AND BudgetMonth=@Month<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当月可用余额\',CONVERT(NVARCHAR,SUM(PlanAmount+AdjustAmount-MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID AND BudgetMonth&lt;=@Month<br /><br />\r\nIF @ControlCycle=\'季度\'<br />\r\nBEGIN<br />\r\nIF @Month IN (\'01\',\'02\',\'03\')<br />\r\nSELECT \'当季可用预算\',CONVERT(NVARCHAR,SUM(PlanAmount+AdjustAmount),1)&nbsp;<br />\r\nFROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID AND BudgetMonth &lt;=\'03\'<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当季已立项\',CONVERT(NVARCHAR,SUM(MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID AND BudgetMonth &lt;=\'03\'<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当季可用余额\',CONVERT(NVARCHAR,SUM(PlanAmount+AdjustAmount-MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID AND BudgetMonth&lt;=\'03\'<br /><br />\r\nIF @Month IN (\'04\',\'05\',\'06\')<br />\r\nSELECT \'当季可用预算\',CONVERT(NVARCHAR,SUM(CASE WHEN BudgetMonth IN (\'04\',\'05\',\'06\') THEN PlanAmount+AdjustAmount end)+SUM(CASE WHEN BudgetMonth&lt;\'04\' THEN PlanAmount+AdjustAmount-MatterAmount end),1)&nbsp;<br />\r\nFROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当季已立项\',CONVERT(NVARCHAR,SUM(MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID AND BudgetMonth IN (\'04\',\'05\',\'06\')<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当月可用余额\',CONVERT(NVARCHAR,SUM(CASE WHEN BudgetMonth IN (\'04\',\'05\',\'06\') THEN PlanAmount+AdjustAmount end)+SUM(CASE WHEN BudgetMonth&lt;\'04\' THEN PlanAmount+AdjustAmount-MatterAmount end)-sum(CASE WHEN BudgetMonth IN (\'04\',\'05\',\'06\') THEN MatterAmount END ),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID&nbsp;<br /><br />\r\nIF @Month IN (\'07\',\'08\',\'09\')<br />\r\nSELECT \'当季可用预算\',CONVERT(NVARCHAR,SUM(CASE WHEN BudgetMonth in (\'07\',\'08\',\'09\') THEN PlanAmount+AdjustAmount end)+SUM(CASE WHEN BudgetMonth&lt;\'07\' THEN PlanAmount+AdjustAmount-MatterAmount end),1)&nbsp;<br />\r\nFROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当季已立项\',CONVERT(NVARCHAR,SUM(MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID AND BudgetMonth IN (\'04\',\'05\',\'06\')<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当月可用余额\',CONVERT(NVARCHAR,SUM(CASE WHEN BudgetMonth IN (\'07\',\'08\',\'09\') THEN PlanAmount+AdjustAmount end)+SUM(CASE WHEN BudgetMonth&lt;\'07\' THEN PlanAmount+AdjustAmount-MatterAmount end)-sum(CASE WHEN BudgetMonth IN (\'07\',\'08\',\'09\') THEN MatterAmount END ),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID&nbsp;<br /><br />\r\nIF @Month IN (\'10\',\'11\',\'12\')<br />\r\nSELECT \'当季可用预算\',CONVERT(NVARCHAR,SUM(CASE WHEN BudgetMonth in (\'10\',\'11\',\'12\') THEN PlanAmount+AdjustAmount end)+SUM(CASE WHEN BudgetMonth&lt;\'10\' THEN PlanAmount+AdjustAmount-MatterAmount end),1)&nbsp;<br />\r\nFROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当季已立项\',CONVERT(NVARCHAR,SUM(MatterAmount),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID AND BudgetMonth IN (\'10\',\'11\',\'12\')<br />\r\nUNION ALL&nbsp;<br />\r\nSELECT \'当月可用余额\',CONVERT(NVARCHAR,SUM(CASE WHEN BudgetMonth IN (\'10\',\'11\',\'12\') THEN PlanAmount+AdjustAmount end)+SUM(CASE WHEN BudgetMonth&lt;\'10\' THEN PlanAmount+AdjustAmount-MatterAmount end)-sum(CASE WHEN BudgetMonth IN (\'10\',\'11\',\'12\') THEN MatterAmount END ),1) FROM dbo.v_ebs_YearDptBudgetReport WHERE DepartmentGUID=@DepartmentGUID AND SubjectGUID=@SubjectGUID&nbsp;<br /><br /><br /><br /><br />\r\nEND&nbsp;<br /><br /><br /><br />\r\nEND<br />\r\n--ELSE&nbsp;<br />\r\n--	BEGIN&nbsp;<br /><br /><br />\r\n--	end <br /><br /><br />\r\nend<br />\r\nSELECT Title,Remarks FROM #UseDtl ORDER BY ID<br /><br />\r\nDROP TABLE #UseDtl<br /><br /><br />\r\nSET NOCOUNT OFF;<br />\r\nEND<br /><br />\r\ngo</p>\r\n<div><br /></div>', 'xianxy', '2018-07-11 17:14:19', 'closed', '2020-03-12 11:23:03', '0000-00-00', '2018-07-11', 'xianxy', '2018-07-11 17:14:57', '', '', '0000-00-00 00:00:00', 'xuf', '2020-03-12 11:23:03', 'done', 'xuf', '2020-03-12 11:23:03', '0');
