INSERT INTO `zt_task` VALUES (868, 0, 30, 127, 274, 1, 0, '收款业务模型优化（先开票后收款与先收款后开票）', 'devel', 0, 0, 4, 0, '2018-08-08', 'closed', '', '', '', '<p><br /></p>\r\n<p>增加直接收款的主表和明细表<br /><br />\r\nif exists (select * from sysobjects where name = \'ims_Receipt\' and type = \'U\')<br />\r\n&nbsp; &nbsp;DROP TABLE ims_Receipt<br />\r\n&nbsp; &nbsp;go<br />\r\n&nbsp; &nbsp;-- =============================================<br />\r\n-- Author:<span> </span>谷国斌<br />\r\n-- Create date: 2018-08-01<br />\r\n-- Description:<span> </span>直接收款表<br />\r\n-- =============================================<br />\r\nCREATE TABLE ims_Receipt(<br /><span> </span>ReceiptGUID uniqueidentifier not null default (NEWSEQUENTIALID()),<br /><span> </span>ContractGUID UNIQUEIDENTIFIER,<br /><span> </span>paymentUnitGUID UNIQUEIDENTIFIER,<br /><span> </span>PaymentUnit NVARCHAR(200),<br /><span> </span>ReceivableUnitGUID UNIQUEIDENTIFIER,<br /><span> </span>ReceivableUnit NVARCHAR(200),<br /><span> </span>ReceiptAmount MONEY,<br /><span> </span>ReceiptDate DATE,&nbsp; --收款日期，允许修改<br /><span> </span>ReceiptBy UNIQUEIDENTIFIER,<br /><span> </span>ReceiptByName NVARCHAR(100),<br /><span> </span>IncomeDate DATE, --入账日期，允许修改<br /><span> </span>ApproveStatus NVARCHAR(10),<br /><span> </span>ApproveBy UNIQUEIDENTIFIER,<br /><span> </span>ApproveByName NVARCHAR(100),<br /><span> </span>ApproveDate DATETIME,<br /><span> </span>CreateBy UNIQUEIDENTIFIER,<br /><span> </span>CreateByName NVARCHAR(100),<br /><span> </span>CreateDate DATETIME,<span> </span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<br /><span> </span>primary key (ReceiptGUID))<br /><br />\r\nGO<br /><br /><br /><br /><br /><br />\r\nif exists (select * from sysobjects where name = \'ims_ReceiptDtl\' and type = \'U\')<br />\r\n&nbsp; &nbsp;DROP TABLE ims_ReceiptDtl<br />\r\n&nbsp; &nbsp;go<br />\r\n&nbsp; &nbsp;-- =============================================<br />\r\n-- Author:<span> </span>谷国斌<br />\r\n-- Create date: 2018-08-01<br />\r\n-- Description:<span> </span>直接收款表<br />\r\n-- =============================================<br />\r\nCREATE TABLE ims_ReceiptDtl(<br /><span> </span>ReceiptDtlGUID uniqueidentifier not null default (NEWSEQUENTIALID()),<br /><span> </span>ReceiptGUID UNIQUEIDENTIFIER,<br /><span> </span>PlanGUID UNIQUEIDENTIFIER,<br /><span> </span>ProjGUID UNIQUEIDENTIFIER,<br /><span> </span>ProjName UNIQUEIDENTIFIER,<br /><span> </span>SubjectGUID UNIQUEIDENTIFIER,<br /><span> </span>SubjectName NVARCHAR(200),<br /><span> </span><br /><span> </span>FundType NVARCHAR(100),<br /><span> </span>FundName NVARCHAR(100),<br /><span> </span>PlanRemainAmount MONEY,--余额<br /><span> </span>ReceiptAmount MONEY,--收款金额<br /><span> </span>TaxRate MONEY,--税率<br /><span> </span>NoTaxAmount MONEY,<br /><span> </span>TaxAmount MONEY,<br /><span> </span>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<br /><span> </span>primary key (ReceiptDtlGUID))<br /><br />\r\nGO</p>', 'lic', '2018-08-08 19:33:28', 'closed', '2020-03-12 11:24:37', '2018-08-03', '0000-00-00', 'lic', '2018-08-08 19:33:47', '', '', '0000-00-00 00:00:00', 'xuf', '2020-03-12 11:24:37', 'done', 'xuf', '2020-03-12 11:24:37', '0');
