INSERT INTO `zt_task` VALUES (873, 0, 30, 127, 279, 1, 0, '补开票据', 'devel', 0, 0, 1, 0, '0000-00-00', 'closed', '', '', '', '<br />\r\n--开票申请主表增加开票类型：1，先开票后收款（为1时，明细表中存PlanGUID）。2：先收款后开票（为2时，明细表中存ReceiptGUID）。<br />\r\nIF NOT EXISTS<br />\r\n(<br />\r\n&nbsp; &nbsp; SELECT *<br />\r\n&nbsp; &nbsp; FROM syscolumns<br />\r\n&nbsp; &nbsp; WHERE id = OBJECT_ID(\'ims_Invoice\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'InvoiceType\'<br />\r\n)<br />\r\n&nbsp; &nbsp; ALTER TABLE dbo.ims_Invoice&nbsp; ADD InvoiceType int<br />\r\nGO<br /><br /><br />\r\n--明细表增加直接收款主表外键。<br />\r\nIF NOT EXISTS<br />\r\n(<br />\r\n&nbsp; &nbsp; SELECT *<br />\r\n&nbsp; &nbsp; FROM syscolumns<br />\r\n&nbsp; &nbsp; WHERE id = OBJECT_ID(\'ims_InvoiceDetail\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'ReceiptGUID\'<br />\r\n)<br />\r\n&nbsp; &nbsp; ALTER TABLE dbo.ims_InvoiceDetail&nbsp; ADD ReceiptGUID UNIQUEIDENTIFIER<br />\r\nGO<br /><br /><p><br />\r\nif exists (select * from sysobjects where name = \'v_ims_Receipt\' and type = \'v\')<br />\r\n&nbsp; &nbsp;drop view v_ims_Receipt<br />\r\n&nbsp; &nbsp;go<br /><br />\r\nCREATE View v_ims_Receipt<br />\r\nas<br />\r\n-- =============================================<br />\r\n-- Author: 谷国斌<br />\r\n-- Create date: 2018-8-13<br />\r\n-- Description: 收款开票视图<br />\r\n-- =============================================<br />\r\n&nbsp;<br />\r\nSELECT&nbsp; a.ReceiptGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ContractGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; paymentUnitGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; PaymentUnit ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ReceivableUnitGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ReceivableUnit ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ReceiptAmount ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ReceiptDate ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ReceiptBy ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ReceiptByName ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; IncomeDate ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ApproveStatus ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ApproveBy ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ApproveByName ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; ApproveDate ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; CreateBy ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; CreateByName ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; CreateDate ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; DepartmentGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; DepartmentName ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; CompanyGUID,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; CASE WHEN a.ReceiptAmount&gt;ISNULL(b.InvoiceAmount,0) THEN \'\' ELSE \'√\' END<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; AS InvoiceStatus<br />\r\nFROM&nbsp; &nbsp; ims_Receipt a<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; LEFT JOIN ( SELECT&nbsp; ReceiptGUID ,<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; SUM(InvoiceAmount) AS InvoiceAmount<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM&nbsp; &nbsp; ims_InvoiceDetail<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE&nbsp; &nbsp;ReceiptGUID IS NOT NULL<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; GROUP BY ReceiptGUID<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ) b ON a.ReceiptGUID = b.ReceiptGUID;<br /><br /><br />\r\nGO</p>\r\n<p>收款列表来自于视图：v_ims_Receipt<img src=\"{5300.png}\" alt=\"\" /></p>\r\n<p><br /></p>\r\n<p><img src=\"{5301.png}\" alt=\"\" /></p>\r\n<div><br /></div>', 'lic', '2018-08-13 18:51:34', 'closed', '2020-03-12 11:24:37', '0000-00-00', '0000-00-00', 'lic', '2018-08-13 18:51:42', '', '', '0000-00-00 00:00:00', 'xuf', '2020-03-12 11:24:37', 'done', 'xuf', '2020-03-12 11:24:37', '0');
