INSERT INTO `zt_task` VALUES (878, 0, 30, 127, 285, 1, 0, '在保存开票登记、收款登记、直接收款功能后，调用存储过程更新收入合同中的累计开票金额和收款金额。（exec p_ims_ContractCalcAmount @BusinessGUID）', 'devel', 1, 0, 0.3, 0, '0000-00-00', 'closed', '', '', '', '<p><br /></p>\r\n<p><br />\r\nif exists (select * from sysobjects where name = \'v_ims_ContractSumView\' and type = \'v\')<br />\r\n&nbsp; &nbsp;drop view v_ims_ContractSumView<br />\r\n&nbsp; &nbsp;go<br /><br /><br /><br />\r\nCREATE VIEW v_ims_ContractSumView<br /><span> </span>AS<br />\r\n&nbsp; -- =============================================<br />\r\n-- Author:<span> </span>谷国斌<br />\r\n-- Create date: 2018-8-15<br />\r\n-- Description:<span> </span>合同汇总视图，用于统计累计开票和累计收款<br />\r\n-- =============================================<br /><br /><span> </span>SELECT&nbsp; a.VoucherGUID AS BusinessGUID ,<br /><span> </span>b.ContractGUID ,<br /><span> </span>a.VoucherAmount ,<br /><span> </span>0.00 AS CollectionAmount<br /><span> </span>FROM&nbsp; &nbsp; dbo.ims_Voucher a<br /><span> </span>INNER JOIN dbo.ims_Invoice b ON a.BusinessGUID = b.InvoiceGUID<br /><span> </span>UNION ALL<br /><span> </span>SELECT&nbsp; a.CollectionGUID ,<br /><span> </span>b.ContractGUID ,<br /><span> </span>0 ,<br /><span> </span>a.CollectionAmount<br /><span> </span>FROM&nbsp; &nbsp; dbo.ims_Collection a<br /><span> </span>INNER JOIN dbo.ims_Invoice b ON a.BusinessGUID = b.InvoiceGUID<br /><span> </span>UNION ALL<br /><span> </span>SELECT&nbsp; ReceiptGUID ,<br /><span> </span>ContractGUID ,<br /><span> </span>0 ,<br /><span> </span>ReceiptAmount<br /><span> </span>FROM&nbsp; &nbsp; ims_Receipt<br /><span> </span>UNION ALL<br /><span> </span>SELECT&nbsp; ContractGUID ,<br /><span> </span>ContractGUID ,<br /><span> </span>0 ,<br /><span> </span>0<br /><span> </span>FROM&nbsp; &nbsp; dbo.ims_Contract<br /><br />\r\nGO<br /><br /><br /><br /><br /><br />\r\nif exists(select * from sysobjects where&nbsp; name = \'p_ims_ContractCalcAmount\' and type = \'P\')<br /><br /><span> </span>DROP PROCEDURE&nbsp; dbo.p_ims_ContractCalcAmount<br />\r\ngo<br />\r\n&nbsp;<br /><br />\r\n-- =============================================<br />\r\n-- Author:<span> </span>谷国斌<br />\r\n-- Create date: 2018-8-15<br />\r\n-- Description:<span> </span>收入合同计算开票金额、实收款金额<br />\r\n-- =============================================<br /><br /><br />\r\nCREATE PROCEDURE p_ims_ContractCalcAmount<br />\r\n(<br />\r\n@BusinessGUID UNIQUEIDENTIFIER --可以是开票登记GUID，收款登记GUID，直接收款GUID，或是合同GUID<br />\r\n)<br />\r\nAS<br />\r\nBEGIN<br /><span> </span>SET NOCOUNT ON;<br /><span> </span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; DECLARE @ContractGUID UNIQUEIDENTIFIER;<br /><span> </span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; SELECT&nbsp; @ContractGUID = ContractGUID<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; FROM&nbsp; &nbsp; v_ims_ContractSumView ds<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; WHERE&nbsp; &nbsp;BusinessGUID = @BusinessGUID;<br /><span> </span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; IF @ContractGUID IS NOT NULL<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; BEGIN&nbsp;<br /><span> </span><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; UPDATE&nbsp; dbo.ims_Contract<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; SET&nbsp; &nbsp; &nbsp;InvoiceAmount = ( SELECT&nbsp; &nbsp; SUM(VoucherAmount)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM&nbsp; &nbsp; &nbsp; v_ims_ContractSumView<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE&nbsp; &nbsp; &nbsp;ContractGUID = @ContractGUID<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; )<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE&nbsp; &nbsp;ContractGUID = @ContractGUID;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; UPDATE&nbsp; ims_Contract<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; SET&nbsp; &nbsp; &nbsp;ReceivabledAmount = ( SELECT&nbsp; &nbsp; SUM(CollectionAmount)<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM&nbsp; &nbsp; &nbsp; v_ims_ContractSumView<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE&nbsp; &nbsp; &nbsp;ContractGUID = @ContractGUID<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; )<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE&nbsp; &nbsp;ContractGUID = @ContractGUID;<br /><span> </span><br /><br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; END;&nbsp;<br /><span> </span><br /><span> </span><br /><span> </span>SET NOCOUNT OFF;<br />\r\nEND<br /><br />\r\ngo</p>', 'lic', '2018-08-15 19:13:23', 'closed', '2020-03-12 11:24:37', '0000-00-00', '0000-00-00', 'lic', '2018-08-15 19:13:33', '', '', '0000-00-00 00:00:00', 'xuf', '2020-03-12 11:24:37', 'done', 'xuf', '2020-03-12 11:24:37', '0');
