INSERT INTO `zt_task` VALUES (1451, 0, 30, 127, 507, 2, 0, '收入合同结算需求 ', 'devel', 0, 0, 1.4, 0, '0000-00-00', 'closed', '', '', '', '<p>\r\n	1.支持工作流和线下审批。\r\n</p>\r\n<p>\r\n	2.收入合同结算发起审批或提交审批后，将收入合同的结算状态改为结算中，审批通过后改为已结算。\r\n</p>\r\n<p>\r\n	3.已结算的收入合同，对合同的审批不允许驳回。\r\n</p>\r\n<p>\r\n	以上逻辑可参考费用合同的结算逻辑。\r\n</p>\r\n<p>\r\n	<br /></p>\r\n<p>\r\n	--以下为结算的相关脚本：<br />\r\nDELETE FROM wf_BizType WHERE BizTypeGUID=\'20982B11-4A97-4756-92D5-A543D20244B8\'<br /><br />\r\nINSERT INTO wf_BizType(BizTypeGUID,BizTypeName,SystemGUID,OrderCode)<br />\r\nVALUES(\'20982B11-4A97-4756-92D5-A543D20244B8\',\'收入合同结算审批\',\'88A41B07-3ECA-E511-B30C-000C292F5F0C\',\'1006\')<br /><br /><br />\r\n--初始化2.sys_GeneralTypeFlowType表<br />\r\nDelete from sys_GeneralTypeFlowType where GeneralTypeFlowGUID=\'4ECA63BD-A677-E911-88CA-00505629CC36\'<br />\r\ninsert into sys_GeneralTypeFlowType(GeneralTypeFlowGUID,GeneralTypeGUID,GeneralTypeFlowCode,GeneralTypeFlowName,BizTypeGUID,BizTypeName,Remarks,FlowType) values (\'4ECA63BD-A677-E911-88CA-00505629CC36\',\'24E8230E-2400-E811-B442-000C293C2531\',\'04\',\'结算审批\',\'20982B11-4A97-4756-92D5-A543D20244B8\',\'收入合同结算审批\',NULL,\'工作流\')<br /><br /><br /><br /><br />\r\ndeclare @systemGUID uniqueidentifier<br />\r\nset @systemGUID = \'88A41B07-3ECA-E511-B30C-000C292F5F0C\'<br /><br />\r\nDECLARE @ParamGroupGUID uniqueidentifier<br /><br />\r\nselect&nbsp; @ParamGroupGUID = ParamGroupGUID from sys_BizParamGroup where GroupName = \'审批模式\' and SystemGUID = @SystemGUID<br /><br /><br />\r\nDELETE sys_BizParamRegist WHERE ParamName=\'ims_SettlementApprove\'<br />\r\nInsert into sys_BizParamRegist(ParamGUID,ParamName, ParamNameChn, ParamGroupGUID, ScopeType, ParamType, ParamWidth, LevelLimit, OrderCode, ShowType, Options, ParamDescription)<br />\r\nvalues(\'0BFECDA1-7492-4363-9455-2B81960CABE6\',\'ims_SettlementApprove\',\'合同结算审批模式\',@ParamGroupGUID,\'公司\',\'参数配置\',NULL,NULL,\'0025\',\'Radio\',\'0,线下审批;1,工作流审批\',\'\')<br /><br /><br />\r\nDELETE FROM sys_BizParamOptionTemplet WHERE ParamName=\'ims_SettlementApprove\'<br /><br />\r\nINSERT INTO sys_BizParamOptionTemplet (ParamName,ParamValue)<br />\r\nVALUES(\'ims_SettlementApprove\',\'1\')<br /><br />\r\nGO<br /><br /><br /><br />\r\n--收入合同增加结算状态字段<br />\r\nIF NOT EXISTS ( SELECT&nbsp; *<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; FROM&nbsp; &nbsp; syscolumns<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE&nbsp; &nbsp;id = OBJECT_ID(\'ims_Contract\')<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND name = \'SettlementStatus\' )<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />\r\n&nbsp; &nbsp; ALTER TABLE ims_Contract ADD SettlementStatus NVARCHAR(100);<br /><br />\r\nGO<br /><br /><br />\r\nif exists (select * from sysobjects where name = \'ims_ContractSettlement\' and type = \'U\')<br />\r\n&nbsp; &nbsp;DROP TABLE ims_ContractSettlement<br />\r\n&nbsp; &nbsp;go<br />\r\n-- =============================================<br />\r\n-- Author:<span> </span>谷国斌<br />\r\n-- Create date: 2019-5-16<br />\r\n-- Description:<span> </span>收入合同结算表<br />\r\n-- =============================================<br />\r\nCREATE TABLE ims_ContractSettlement(<br />\r\nContSettlementGUID uniqueidentifier not null default (NEWSEQUENTIALID()),<br />\r\nCompanyGUID uniqueidentifier,<br />\r\nContractGUID UNIQUEIDENTIFIER,<br />\r\nApproveStatus NVARCHAR(20),<br />\r\nSettlementDate DATE, --结算日期，允许修改<br />\r\nContractAmount MONEY,<br />\r\nSettlementAmount MONEY,<br />\r\nSettlAdjustAmount MONEY,<br />\r\nOperatorGUID UNIQUEIDENTIFIER,<br />\r\nOperator NVARCHAR(200),<br />\r\nDepartmentGUID UNIQUEIDENTIFIER,<br />\r\nDepartmentName NVARCHAR(200),<br />\r\nRemarks NVARCHAR(2000),<br />\r\nApproveBy UNIQUEIDENTIFIER,<br />\r\nApproveByName NVARCHAR(200),<br />\r\nApproveDate DATETIME,<br />\r\nCreateTime DATETIME,--创建日期，记录服务器当前日期。前端不展示。<br />\r\nprimary key (ContSettlementGUID))<br /><br />\r\nGO<br /><br /><br /></p>', 'lic', '2019-05-16 19:05:10', 'closed', '2020-03-12 11:24:39', '0000-00-00', '0000-00-00', 'lic', '2019-05-20 16:42:41', '', '', '0000-00-00 00:00:00', 'xuf', '2020-03-12 11:24:39', 'done', 'xuf', '2020-03-12 11:24:39', '0');
