INSERT INTO `zt_task` VALUES (1583, 0, 17, 247, 547, 1, 0, '变更签证保存时，需要将变更类型中的是否计入成本属性保存在变更记录中。 ', 'devel', 1, 0, 0.3, 0, '0000-00-00', 'closed', '', '', '', '<div>\r\n	<br />\r\n--变更表中记录是否计入成本的属性<br />\r\nIF NOT EXISTS<br />\r\n(<br />\r\n    SELECT *<br />\r\n    FROM syscolumns<br />\r\n    WHERE id = OBJECT_ID(\'tps_ContractAlter\')<br />\r\n          AND name = \'IsNotIncludedCost\'<br />\r\n)<br />\r\n    ALTER TABLE tps_ContractAlter ADD IsNotIncludedCost bit<br />\r\nGO\r\n</div>\r\n<div>\r\n	--处理历史数据\r\n</div>\r\n<div>\r\n	UPDATE a SET IsNotIncludedCost= CASE WHEN ISNULL(c.GeneralTypePropertyValue,1)=1 THEN 1 ELSE 0 END <br />\r\nFROM tps_ContractAlter a <br />\r\n LEFT JOIN<br />\r\n    (<br />\r\n        SELECT t.GeneralCategoryGUID,<br />\r\n               t.GeneralTypePropertyValue<br />\r\n        FROM sys_GeneralCategory y<br />\r\n            INNER JOIN sys_GeneralTypeValue t<br />\r\n                ON y.GeneralCategoryGUID = t.GeneralCategoryGUID<br />\r\n        WHERE t.GeneralTypePropertyGUID IN (<br />\r\n                                               SELECT GeneralTypePropertyGUID<br />\r\n                                               FROM dbo.sys_GeneralTypeProperty<br />\r\n                                               WHERE GeneralTypeGUID IN (<br />\r\n                                                                            SELECT GeneralTypeGUID<br />\r\n                                                                            FROM dbo.sys_GeneralType<br />\r\n                                                                            WHERE GeneralTypeCode = \'cms_ContractAlter\'<br />\r\n                                                                        )   AND PropertyCode=\'IsNotIncludedCost\'<br />\r\n                                           )<br />\r\n    ) c<br />\r\n        ON a.AlterTypeGUID = c.GeneralCategoryGUID\r\n</div>\r\n<div>\r\n	<br /></div>\r\n<br />', 'lic', '2019-07-03 14:42:27', 'closed', '2020-03-12 11:14:19', '0000-00-00', '0000-00-00', 'lic', '2019-07-03 14:42:38', '', '', '0000-00-00 00:00:00', 'xuf', '2020-03-12 11:14:19', 'done', 'xuf', '2020-03-12 11:14:19', '0');
