INSERT INTO `zt_task` VALUES (2337, 0, 60, 0, 720, 1, 0, '组织架构字段调整，添加规则调整', 'devel', 3, 0.5, 0.5, 0, '2020-07-03', 'closed', '', '', '', '1、新增组织，companyid不能为空<br />&nbsp; &nbsp; &nbsp; &nbsp; 公司：companyid =&nbsp; 所属公司id(只有root的id=companyid)<br />&nbsp; &nbsp; &nbsp; &nbsp; 公司所属部门：companyid = parentid（所属公司）<br /><p>&nbsp; &nbsp; &nbsp; &nbsp; 部门所属部门：companyid同样为所属公司id，注意不等于parentid</p>\r\n<p>2、字段更改</p>\r\n<p>&nbsp; &nbsp; &nbsp;组织type：应改为 0 公司；1 部门</p>\r\n<p>3、level改为从0开始</p>\r\n<p>4、删除tel字段</p>\r\n<p>5、增加full_name_path字段：</p>\r\n<p>&nbsp; &nbsp; &nbsp;&nbsp;规则：&nbsp;上级 <span>full_name_path + \"&gt;&gt;\" + 本级name&nbsp; 如果没有上级 ：<span>full_name_path =&nbsp;</span>name</span></p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;ex:总部&gt;&gt;西南区域公司</p>\r\n<p>6、数据库字段空值，统一为null</p>', 'jianxb', '2020-07-03 09:25:47', 'closed', '2020-07-06 09:56:46', '2020-07-03', '0000-00-00', 'wangl', '2020-07-03 13:51:30', '', '', '0000-00-00 00:00:00', 'jianxb', '2020-07-06 09:56:46', 'done', 'jianxb', '2020-07-06 09:56:46', '0');
INSERT INTO `zt_task` VALUES (2338, 0, 60, 868, 720, 1, 0, '岗位信息调整', 'devel', 3, 0.4, 0.5, 0, '2020-07-03', 'closed', '', '', '', '<p>1、岗位表：删除岗位全称字段</p>\r\n<p>2、分页组件尺寸问题调整</p>\r\n<p>3、上移/下移显示</p>\r\n<p>4、岗位表删除层级编码、岗位编码、删除岗位职责字段</p>\r\n<p>5、前端列表点击岗位名称进入修改页面，而不是编码</p>\r\n<p>6、同一组织下，岗位名称不应该重复</p>\r\n<p>7、岗位，人员列表加载数据后，默认选中第一条</p>\r\n<p>8、组织下有岗位，不能删除组织</p>', 'jianxb', '2020-07-03 11:28:52', 'closed', '2020-07-10 09:18:07', '2020-07-03', '0000-00-00', 'wangl', '2020-07-03 20:00:37', '', '', '0000-00-00 00:00:00', 'jianxb', '2020-07-10 09:18:07', 'done', 'jianxb', '2020-07-10 09:18:07', '0');
