INSERT INTO `zt_task` VALUES (2370, 0, 20, 0, 745, 1, 0, '在线编辑需求调整 ', 'devel', 3, 1, 1, 0, '2020-07-10', 'closed', '', '', '', '<p><span style=\"color:#000000;font-family:\'微软雅黑\', Verdana;\">2020-7-9 铁建华中《文档在线编辑功能》演示所提优化//参与人：代春中、尹慧敏、李成</span></p>\r\n<p><br /></p>\r\n<div style=\"margin:0px;padding:0px;color:#000000;font-size:14px;font-family:\'微软雅黑\', Verdana;\">\r\n  <div style=\"margin:0px;padding:0px;\">\r\n    <div style=\"margin:0px;padding:0px;border:0px;\">①主模板筛选数据来源下拉框添加“所有”选项。</div>\r\n    <div style=\"margin:0px;padding:0px;border:0px;\">②系统admin不受在线编辑板块权限控制，admin可查看、编辑、使用所有模板，以便后期维护。</div>\r\n    <div style=\"margin:0px;padding:0px;border:0px;\">③模板发布后列表没有刷新//主模板、标准文件</div>\r\n    <div style=\"margin:0px;padding:0px;border:0px;\">④模板发布后，生成的历史文件未将GUID和文件后缀（docx）分开。//如下图：</div>\r\n    <div style=\"margin:0px;padding:0px;\"><img src=\"{18736.png}\" alt=\"\" /><br /></div>\r\n  </div>\r\n</div>\r\n<div style=\"margin:0px;padding:0px;color:#000000;font-size:14px;font-family:\'微软雅黑\', Verdana;\">\r\n  <div style=\"margin:0px;padding:0px;\">\r\n    <div style=\"margin:0px;padding:0px;\"><span style=\"font-family:\'微软雅黑\', Verdana;\">⑤标准文件同步数据时，选择业务记录后，默认勾选所选择业务的所有数据。</span></div>\r\n    <div style=\"margin:0px;padding:0px;border:0px;\">⑥标准文件同步数据详情页中的原始数据板块再添加一列“数据来源”，标识每个书签的数据来源。</div>\r\n  </div>\r\n</div>\r\n<p><br /></p>', 'lic', '2020-07-10 10:37:29', 'closed', '2020-07-17 11:41:18', '2020-07-10', '0000-00-00', 'lic', '2020-07-10 17:24:16', '', '', '0000-00-00 00:00:00', 'wangyt', '2020-07-17 11:41:18', 'done', 'wangyt', '2020-07-17 11:41:18', '0');
