set define off;
--EXCEL导出文件信息初始化
delete UDP_EXPORT_EXCEL where ID in  (select t.ID from (  select * from UDP_EXPORT_EXCEL where ID in (SELECT EXCEL_ID FROM UDP_EXPORT_SHEET WHERE  ID= 'a221925b-f54a-69c0-e053-0100007f1fa8') ) t );
--EXCEL导出工作簿信息初始化
delete UDP_EXPORT_SHEET where ID in  (select t.ID from (  select * from UDP_EXPORT_SHEET where ID ='a221925b-f54a-69c0-e053-0100007f1fa8' ) t );
--EXCEL导出工作簿关联表字段信息初始化
delete UDP_EXPORT_SHEET_FIELD where ID in  (select t.ID from (select * from UDP_EXPORT_SHEET_FIELD  where EXPORT_SHEET_ID ='a221925b-f54a-69c0-e053-0100007f1fa8' ) t );
--EXCEL导出字段信息初始化
delete UDP_EXPORT_FIELD where ID in  (select t.ID from (select * from UDP_EXPORT_FIELD where ID in (select  EXPORT_FIELD_ID  from UDP_EXPORT_SHEET_FIELD  where EXPORT_SHEET_ID  ='a221925b-f54a-69c0-e053-0100007f1fa8' )) t );
--EXCEL导出数据源信息初始化
delete UDP_COMPONENT_DATA_SOURCE where ID in  (select t.ID from ( select UDP_COMPONENT_DATA_SOURCE.* from UDP_COMPONENT_DATA_SOURCE inner join (select data_source from UDP_EXPORT_SHEET where ID='a221925b-f54a-69c0-e053-0100007f1fa8') sourceids on  UDP_COMPONENT_DATA_SOURCE.id=sourceids.data_source  ) t );
--EXCEL导出数据源存储过程初始化
delete UDP_PROCEDURE_REGISTRATION where ID in  (select t.ID from (select UDP_PROCEDURE_REGISTRATION.* from UDP_PROCEDURE_REGISTRATION inner join (select UDP_COMPONENT_DATA_SOURCE.DATA_SOURCE from UDP_COMPONENT_DATA_SOURCE inner join (select data_source from UDP_EXPORT_SHEET where ID='a221925b-f54a-69c0-e053-0100007f1fa8') sourceids
     on UDP_COMPONENT_DATA_SOURCE.id=sourceids.data_source where UDP_COMPONENT_DATA_SOURCE.DATA_SOURCE_TYPE='procedure') PROCCodes
     on UDP_PROCEDURE_REGISTRATION.code=PROCCodes.DATA_SOURCE ) t );
--EXCEL导出数据源存储过程参数初始化
delete UDP_PROCEDURE_PARAMETER where ID in  (select t.ID from ( select UDP_PROCEDURE_PARAMETER.* from UDP_PROCEDURE_PARAMETER inner join (select UDP_PROCEDURE_REGISTRATION.id from UDP_PROCEDURE_REGISTRATION inner join (select UDP_COMPONENT_DATA_SOURCE.DATA_SOURCE from UDP_COMPONENT_DATA_SOURCE inner join (select data_source from UDP_EXPORT_SHEET where ID='a221925b-f54a-69c0-e053-0100007f1fa8') sourceids
    on UDP_COMPONENT_DATA_SOURCE.id=sourceids.data_source where UDP_COMPONENT_DATA_SOURCE.DATA_SOURCE_TYPE='procedure') PROCCodes
    on UDP_PROCEDURE_REGISTRATION.code=PROCCodes.DATA_SOURCE) PROCIds
    on UDP_PROCEDURE_PARAMETER.PROCEDURE_REGISTRATION_ID=PROCIds.id) t );
